function addTraitToTargets(name){ 
    if (null !== targets)
    {
        targets.forEach(function(c) {
            if (c.addTraitByName(name))
                print("added trait " + name + " to " + c.getName());
            else
                print("failed to add trait " + name + " to " + c.getName());
        });
    }
    return 0; 
}
