com.d20pro.temp_extraction.plugin.featur.script:
/**
 * returns the caster's level or the maximum value, whichever is lower.
 */

function getCasterLevelOrLower(max) {
    return Math.min(caster.getClasses().accessClass(feature.getBehavior().getFeatureUsage().getSpellDataHolder().getSpellcastingClassName()).getLevel(), max);
}
