com.d20pro.temp_extraction.plugin.featur.script:
/**
 * returns the caster's total levels (for all classes) or the minimum value which ever is lower.
 */

function getCasterLevelOrMax(max) {
    return Math.max(caster.getClasses().accessClass(feature.getBehavior().getFeatureUsage().getSpellDataHolder().getSpellcastingClassName()).getLevel(), max);
}
