com.d20pro.temp_extraction.plugin.featur.script:
function removeAid(){ 

    if (null !== targets)
        targets.forEach(function(c) {
            if (null !== c)
            {
                c.setHPMax( c.getHPMax() - ( 5 * (getFeatureLevel() - 1) ) );

                if (c.getHP() > c.getHPMax() )
                    c.setHP( c.getHPMax() );
            }
        });


    return 5 * (getFeatureLevel() - 1);
}
