function removeTraitFromTargets(name){ 
    if (null !== targets)
    {
        targets.forEach(function(c) {
            if (c.removeTraitByName(name))
                print("removed trait " + name + " to " + c.getName());
            else
                print("failed to remove trait " + name + " to " + c.getName());
        });
    }
    return 0; 
}
