com.d20pro.temp_extraction.plugin.featur.script:
function rollDieToLogFor(roll, reason){ 
    var rollResult;    
    var result;

    if (roll == "ad") {
        roll = "1d20";
        rollResult = caster.rollDice(roll);

        addToRollsLog("");

        var dice = rollResult.getDice();

        var r1 = rollResult.getDice().simpleRoll();
        var r2 = rollResult.getDice().simpleRoll();

        addToRollsLog(caster.getName() + " rolled " + r1 + " / " + r2 + " for Advantage/Disadvantage against " + reason);

        // this can also be extended to actually check against a stat or difficulty -- excellent for meta
        // challenges where all you really need are a "did it work" type of result.

    } else {
        rollResult = caster.rollDice(roll);

        result = rollResult.total();

        addToRollsLog("");
        addToRollsLog(caster.getName() + " rolled " + roll + " for " + result);

        if (rollResult.hasIndividualRolls())
        {
            var i = 0;
            addToRollsLog(caster.getName() + " rolled " + roll + " for a total of " + result + " against " + reason);
            var rolls = "";
            rollResult.getIndividualRolls().forEach( function(r) {
                if (i !== 0)
                    rolls +=  ", " + r;
                else
                    rolls += r;

                i++;
            });
            addToRollsLog("individual rolls: " + rolls);
        }
    }

    return 0;
}
